function loadBooks(){booksList.forEach(function(o,n){const a=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${o.isActive?" active":""}">\n                    <a target="_blank" href="${o.link}">\n                        <img class="img-fluid mx-auto d-block" src="${o.img}" alt="slide ${n+1}">\n                    </a>\n            </div>`;$("#divBooks").append(a)})}window.onload=function(){let o="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(o="es-sv/min");getScript(`../js/data/translations/${o}/booksList.js`).then(()=>{loadBooks()}).catch(o=>{console.error(o)})};