async function fetchData(e){try{const t=await fetch(e);return await t.json()}catch(e){console.error("Error loading JSON:",e)}}function loadBooks(e){const t="../img/books/",o=document.createDocumentFragment();e.forEach((e=>{const n=e.isActive?" active":"",a=document.createElement("div");a.className=`carousel-item col-12 col-sm-6 col-md-4 col-lg-2${n}`,a.innerHTML=`<a target="_blank" href="https://${e.link}">\n                                <picture>\n                                    <source srcset="${t}${e.img}.webp" type="image/webp">\n                                    <source srcset="${t}${e.img}.jpg" type="image/jpeg">\n                                    <img class="img-fluid mx-auto d-block" src="${t}${e.img}.jpg" alt="${e.title}">\n                                </picture>\n                            </a>`,o.appendChild(a)})),document.getElementById("divBooks").appendChild(o),$("#booksCarousel").on("slide.bs.carousel",(function(e){let t=$(e.relatedTarget).index(),o=$(".carousel-item").length;if(t>=o-6){let n=7-(o-t);for(let t=0;t<n;t++)"left"==e.direction?$(".carousel-item").eq(t).appendTo(".carousel-inner"):$(".carousel-item").eq(0).appendTo(".carousel-inner")}}))}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min";loadBooks((await fetchData(`../js/data/translations/${e}/booksList.json`)).booksList)}catch(e){console.error(e)}}));