function loadBooks(){booksList.forEach((function(o,n){const t=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${o.isActive?" active":""}">\n                    <a target="_blank" href="${o.link}">\n                        <img class="img-fluid mx-auto d-block" src="${o.img}" alt="${o.title}">\n                    </a>\n            </div>`;document.getElementById("divBooks").innerHTML+=t}))}window.addEventListener("DOMContentLoaded",(o=>{let n="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(n="es-sv/min");getScript(`../js/data/translations/${n}/booksList.js`).then((()=>{loadBooks()})).catch((o=>{console.error(o)}))}));