function loadBooks(e){const t="../img/books/",n=document.createDocumentFragment();e.forEach((e=>{const o=e.isActive?" active":"",a=document.createElement("div");a.className=`carousel-item col-12 col-sm-6 col-md-4 col-lg-2${o}`,a.innerHTML=`<a target="_blank" href="https://${e.link}">\n                                <picture>\n                                    <source srcset="${t}${e.img}.webp" type="image/webp">\n                                    <source srcset="${t}${e.img}.jpg" type="image/jpeg">\n                                    <img class="img-fluid mx-auto d-block" src="${t}${e.img}.jpg" alt="${e.title}">\n                                </picture>\n                            </a>`,n.appendChild(a)})),document.getElementById("divBooks").appendChild(n)}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min",t="../js/data/translations/";loadBooks((await fetchData(`${t}${e}/booksList.json`)).booksList)}catch(e){console.error(e)}}));