function loadBooks(){booksList.forEach(function(o,i){const l=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${o.isActive?" active":""}">\n                    <a target="_blank" href="${o.link}">\n                        <img class="img-fluid mx-auto d-block" src="${o.img}" alt="slide ${i+1}">\n                    </a>\n            </div>`;$("#divBooks").append(l)})}loadBooks();