function loadBooks(){booksList.forEach((function(e,n){const o=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${e.isActive?" active":""}">\n                    <a target="_blank" href="${e.link}">\n                        <picture>\n                            <source\n                                srcset="${e.img.replace(".jpg",".webp")}"\n                                type="image/webp">\n                            <source\n                                srcset="${e.img}" \n                                type="image/jpeg">\n                            <img class="img-fluid mx-auto d-block" src="${e.img}" alt="${e.title}">\n                        </picture>\n                    </a>\n            </div>`;document.getElementById("divBooks").innerHTML+=o}))}window.addEventListener("DOMContentLoaded",(e=>{let n="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(n="es-sv/min");getScript(`../js/data/translations/${n}/booksList.js`).then((()=>{loadBooks()})).catch((e=>{console.error(e)}))}));