async function fetchData(e){try{const t=await fetch(e);return await t.json()}catch(e){console.error(e)}}function loadBooks(e){const t="../img/books/",o=document.createDocumentFragment();e.forEach((e=>{const a=e.isActive?" active":"",c=document.createElement("div");c.className=`carousel-item col-12 col-sm-6 col-md-4 col-lg-2${a}`,c.innerHTML=`<a target="_blank" href="https://${e.link}"><picture><source srcset="${t}${e.img}.webp" type="image/webp"><source srcset="${t}${e.img}.jpg" type="image/jpeg"><img class="img-fluid mx-auto d-block" loading='lazy' src="${t}${e.img}.jpg" alt="${e.title}"></picture></a>`,o.appendChild(c)})),$("#divBooks").append(o),$("#booksCarousel").on("slide.bs.carousel",(e=>{const t=".carousel-item",o=$(e.relatedTarget),a=$(t).length;let c=o.index();if(c>=a-6){let o=7-(a-c);for(let a=0;a<o;a++)$(t).eq("left"==e.direction?a:0).appendTo(".carousel-inner")}}))}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=new URL(window.location.href).searchParams.get("lang");loadBooks((await fetchData(`../js/i18n/${e||"en"}/books.json`)).booksList)}catch(e){console.error(e)}}));