async function fetchData(e){try{const t=await fetch(e);return await t.json()}catch(e){console.error("Error loading JSON:",e)}}function loadBooks(e){const t="../img/books/",o=document.createDocumentFragment();e.forEach((e=>{const a=e.isActive?" active":"",n=document.createElement("div");n.className=`carousel-item col-12 col-sm-6 col-md-4 col-lg-2${a}`,n.innerHTML=`<a target="_blank" href="https://${e.link}"><picture><source srcset="${t}${e.img}.webp" type="image/webp"><source srcset="${t}${e.img}.jpg" type="image/jpeg"><img class="img-fluid mx-auto d-block" loading='lazy' src="${t}${e.img}.jpg" alt="${e.title}"></picture></a>`,o.appendChild(n)})),document.getElementById("divBooks").appendChild(o),$("#booksCarousel").on("slide.bs.carousel",(function(e){let t=$(e.relatedTarget).index(),o=$(".carousel-item").length;if(t>=o-6){let a=7-(o-t);for(let t=0;t<a;t++)"left"==e.direction?$(".carousel-item").eq(t).appendTo(".carousel-inner"):$(".carousel-item").eq(0).appendTo(".carousel-inner")}}))}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min";loadBooks((await fetchData(`../js/data/translations/${e}/booksList.json`)).booksList)}catch(e){console.error(e)}}));