function loadBooks(n){n.forEach((function(n){const e="../img/books/",o=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${n.isActive?" active":""}">\n                    <a target="_blank" href="https://${n.link}">\n                        <picture>\n                            <source\n                                srcset="${e}${n.img}.webp"\n                                type="image/webp">\n                            <source\n                                srcset="${e}${n.img}.jpg"\n                                type="image/jpeg">\n                            <img class="img-fluid mx-auto d-block" src="${e}${n.img}.jpg" alt="${n.title}">\n                        </picture>\n                    </a>\n            </div>`;document.getElementById("divBooks").innerHTML+=o}))}window.addEventListener("DOMContentLoaded",(()=>{let n="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(n="es-sv/min");fetchData(`../js/data/translations/${n}/booksList.json`).then((n=>{loadBooks(n.booksList)})).catch((n=>{console.error(n)}))}));