function loadBooks(){booksList.forEach((function(e){const n="../img/books/",o=`<div class="carousel-item col-12 col-sm-6 col-md-4 col-lg-2${e.isActive?" active":""}">\n                    <a target="_blank" href="https://${e.link}">\n                        <picture>\n                            <source\n                                srcset="${n}${e.img}.webp"\n                                type="image/webp">\n                            <source\n                                srcset="${n}${e.img}.jpg"\n                                type="image/jpeg">\n                            <img class="img-fluid mx-auto d-block" src="${n}${e.img}" alt="${e.title}">\n                        </picture>\n                    </a>\n            </div>`;document.getElementById("divBooks").innerHTML+=o}))}window.addEventListener("DOMContentLoaded",(()=>{let e="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(e="es-sv/min");getScript(`../js/data/translations/${e}/booksList.js`).then((()=>{loadBooks()})).catch((e=>{console.error(e)}))}));