async function fetchData(e){try{const o=await fetch(e);return await o.json()}catch(e){console.error("Error loading JSON:",e)}}function loadBooks(e){const o="../img/books/",t=document.createDocumentFragment();e.forEach((e=>{const n=e.isActive?" active":"",a=document.createElement("div");a.className=`carousel-item col-12 col-sm-6 col-md-4 col-lg-2${n}`,a.innerHTML=`<a target="_blank" href="https://${e.link}">\n                                <picture>\n                                    <source srcset="${o}${e.img}.webp" type="image/webp">\n                                    <source srcset="${o}${e.img}.jpg" type="image/jpeg">\n                                    <img class="img-fluid mx-auto d-block" src="${o}${e.img}.jpg" alt="${e.title}">\n                                </picture>\n                            </a>`,t.appendChild(a)})),document.getElementById("divBooks").appendChild(t),$("#booksCarousel").on("slide.bs.carousel",(function(e){var o=$(e.relatedTarget).index(),t=$(".carousel-item").length;if(o>=t-6)for(var n=7-(t-o),a=0;a<n;a++)"left"==e.direction?$(".carousel-item").eq(a).appendTo(".carousel-inner"):$(".carousel-item").eq(0).appendTo(".carousel-inner")}))}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min";loadBooks((await fetchData(`../js/data/translations/${e}/booksList.json`)).booksList)}catch(e){console.error(e)}}));