async function fetchData(e){try{const n=await fetch(e);return await n.json()}catch(e){console.error("Error loading JSON:",e)}}function loadLegends(e){const n="../img/legends/",t=document.createDocumentFragment();e.forEach((e=>{const a=e.isActive?" active":"",i=document.createElement("div");i.className=`carousel-item col-md-4${a}`,i.innerHTML=`<div class="card"><div class="img-thumbnail"><a class="text-warning legend-link" target="_blank" href="https://${e.link}"><picture><source srcset="${n}${e.img}.webp" type="image/webp"><source srcset="${n}${e.img}.jpg" type="image/jpeg"><img src="${n}${e.img}.jpg" loading='lazy' alt="${e.title}" class="cards-row"></picture><div class="caption"><p><b>${e.title}</b></p></div></a></div></div>`,t.appendChild(i)})),document.getElementById("divLegends").appendChild(t)}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min";loadLegends((await fetchData(`../js/i18n/${e}/legendsList.json`)).legendsList)}catch(e){console.error(e)}$("#legendsCarousel").on("slide.bs.carousel",(function(e){let n=$(e.relatedTarget).index(),t=$(".carousel-item").length;if(n>=t-2){let a=3-(t-n);for(let n=0;n<a;n++)"left"==e.direction?$(".carousel-item").eq(n).appendTo(".carousel-inner"):$(".carousel-item").eq(0).appendTo(".carousel-inner")}}))}));