function loadLegends(e){const n="../img/legends/",t=document.createDocumentFragment();e.forEach((e=>{const a=e.isActive?" active":"",s=document.createElement("div");s.className=`carousel-item col-md-4${a}`,s.innerHTML=`<div class="card">\n                                    <div class="img-thumbnail">\n                                        <a class="text-warning legend-link" target="_blank" href="https://${e.link}">\n                                            <picture>\n                                                <source srcset="${n}${e.img}.webp" type="image/webp">\n                                                <source srcset="${n}${e.img}.jpg" type="image/jpeg">\n                                                <img src="${n}${e.img}.jpg" alt="${e.title}" class="cards-row">\n                                            </picture>\n                                            <div class="caption">\n                                                <p><b>${e.title}</b></p>\n                                            </div>\n                                        </a>\n                                    </div>\n                                </div>`,t.appendChild(s)})),document.getElementById("divLegends").appendChild(t)}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min",n="../js/data/translations/";loadLegends((await fetchData(`${n}${e}/legendsList.json`)).legendsList)}catch(e){console.error(e)}}));