function loadLegends(){legendsList.forEach((n=>{const e=`<div class="carousel-item col-md-4${n.isActive?" active":""}">\n            <div class="card">\n            <div class="img-thumbnail">\n                <a class="text-warning" target="_blank" href="${n.link}">\n                    <img src="${n.img}" alt="Cadejo" class="cards-row">\n                    <div class="caption">\n                        <p>${n.title}</p>\n                    </div>\n                </a>\n            </div>\n            </div>\n        </div>`;document.getElementById("divLegends").innerHTML+=e}))}window.addEventListener("DOMContentLoaded",(n=>{let e="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(e="es-sv/min");getScript(`../js/data/translations/${e}/legendsList.js`).then((()=>{loadLegends()})).catch((n=>{console.error(n)}))}));