async function fetchData(e){try{const t=await fetch(e);return await t.json()}catch(e){console.error("Error loading JSON:",e)}}function loadLegends(e){const t="../img/legends/",n=document.createDocumentFragment();e.forEach((e=>{const a=e.isActive?" active":"",i=document.createElement("div");i.className=`carousel-item col-md-4${a}`,i.innerHTML=`<div class="card"><div class="img-thumbnail"><a class="text-warning legend-link" target="_blank" href="https://${e.link}"><picture><source srcset="${t}${e.img}.webp" type="image/webp"><source srcset="${t}${e.img}.jpg" type="image/jpeg"><img src="${t}${e.img}.jpg" loading='lazy' alt="${e.title}" class="cards-row"></picture><div class="caption"><p><b>${e.title}</b></p></div></a></div></div>`,n.appendChild(i)})),document.getElementById("divLegends").appendChild(n)}window.addEventListener("DOMContentLoaded",(async()=>{try{const e=(window.navigator.userLanguage||window.navigator.language).includes("es")?"es-sv/min":"en-us/min";loadLegends((await fetchData(`../js/data/translations/${e}/legendsList.json`)).legendsList)}catch(e){console.error(e)}$("#legendsCarousel").on("slide.bs.carousel",(function(e){let t=$(e.relatedTarget).index(),n=$(".carousel-item").length;if(t>=n-2){let a=3-(n-t);for(let t=0;t<a;t++)"left"==e.direction?$(".carousel-item").eq(t).appendTo(".carousel-inner"):$(".carousel-item").eq(0).appendTo(".carousel-inner")}}))}));