function loadLegends(){legendsList.forEach((e=>{const n=`<div class="carousel-item col-md-4${e.isActive?" active":""}">\n            <div class="card">\n            <div class="img-thumbnail">\n                <a class="text-warning" target="_blank" href="${e.link}">\n                    <picture>\n                        <source\n                            srcset="${e.img.replace(".jpg",".webp")}"\n                            type="image/webp">\n                        <source\n                            srcset="${e.img}" \n                            type="image/jpeg">\n                        <img src="${e.img}" alt="${e.title}" class="cards-row">\n                    </picture>\n                    <div class="caption">\n                        <p><b>${e.title}</b></p>\n                    </div>\n                </a>\n            </div>\n            </div>\n        </div>`;document.getElementById("divLegends").innerHTML+=n}))}window.addEventListener("DOMContentLoaded",(e=>{let n="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(n="es-sv/min");getScript(`../js/data/translations/${n}/legendsList.js`).then((()=>{loadLegends()})).catch((e=>{console.error(e)}))}));