function loadLegends(n){n.forEach((n=>{const e="../img/legends/",i=`<div class="carousel-item col-md-4${n.isActive?" active":""}">\n            <div class="card">\n            <div class="img-thumbnail">\n                <a class="text-warning legend-link" target="_blank" href="https://${n.link}">\n                    <picture>\n                        <source\n                            srcset="${e}${n.img}.webp"\n                            type="image/webp">\n                        <source\n                            srcset="${e}${n.img}.jpg"\n                            type="image/jpeg">\n                        <img src="${e}${n.img}.jpg" alt="${n.title}" class="cards-row">\n                    </picture>\n                    <div class="caption">\n                        <p><b>${n.title}</b></p>\n                    </div>\n                </a>\n            </div>\n            </div>\n        </div>`;document.getElementById("divLegends").innerHTML+=i}))}window.addEventListener("DOMContentLoaded",(()=>{let n="en-us/min";(window.navigator.userLanguage||window.navigator.language).includes("es")&&(n="es-sv/min");fetchData(`../js/data/translations/${n}/legendsList.json`).then((n=>{loadLegends(n.legendsList)})).catch((n=>{console.error(n)}))}));